draw((0,-4)--(0,6),Arrows(4));

int i;
for(i=-4;i<=1;++i)

{

draw((i,-0.3)--(i,0.3));

}
for(i=-3;i<=5;++i)

{

draw((-0.3,i)--(0.3,i));

}

label("$a$",(2.5,0));
label("$b$",(0,6.5));[/asy]

