label("$P$",(5,3),3N);
label("5",(0,3)--(5,3),N);
label("3",(5,0)--(5,3),E);
label("14",(7,-3));
draw((5,3)--(14,3),black+linewidth(1)+dashed);
label("$A$",(0,15),NW);
label("$B$",(14,15),NE);
label("$C$",(14,0),SE);
label("$D$",(0,0),SW);
