A=(0,1.73); B=(1,1.73); C=(1.43,0.87); D=(1,0); E=(0,0); F=(-0.43, 0.87);
draw(A--B--C--D--E--F--cycle,linewidth(1));
draw(A--C--E--cycle);
draw(B--D--F--cycle);
draw(A--D);
draw(B--E);
draw(C--F);
label("A", A, NW);
label("B", B, NE);
label("C",(1.55,0.87),E);
label("D",D,SE);
label("E",E,SW);
label("F",F,W);
