label("A",(2,2),SW);
label("B",(4,2),SE);
label("C",(3,1), SE);
label("C",(3,3), SE);
label("C",(3,4), SE);
label("C",(3,5), SE);
label("C",(4,4), SE);
label("C",(2,4), SE);
[/asy]

